import React from "react";

const botTypeClasses = {
  Assault: "üéñ",
  Defender: "üõ°",
  Support: "‚ûï",
  Medic: "üöë",
  Witch: "ü™Ñ",
  Captain: "‚≠ê",
};

function BotItem({ bot, clickHandler, handleDelete }) {
  return (
    <div>
      <div className="ui-card" key={bot.id} onClick={() => clickHandler(bot)}>
        <div className="image">
          <img alt="bot" src={bot.avatar_url} />
        </div>
        <div>
          <div>
            {bot.name}
            <i className={botTypeClasses[bot.bot_class]} />
          </div>
          <div>
            <small>{bot.catchphrase.substring(0, )}</small>
          </div>
        </div>
        <div>
          <span>
            ‚õë{bot.health}
          </span>
          <span>
            ‚ö°{bot.damage}
          </span>
          <span>
            üõ°{bot.armor}
          </span>
          <span>
            <div>
              <button
                title="delete this bot"
                className="delete-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  handleDelete(bot);
                }}>
                delete
              </button>
            </div>
          </span>
        </div>
      </div>
    </div>
  );
}

export default BotItem;
